import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { LikesClicked, DisLikesClicked, loadPosts } from "./postSlice";

export function PostList() {
  const { status, posts, error } = useSelector((state) => state.posts);
  const dispatch = useDispatch();
  //const posts = useSelector((state) => { return state.posts});

  useEffect(() => {
    (async () => {
      if (status === "idle") {
        dispatch(loadPosts());
      }
    })();
  }, [status, dispatch]);

  return (
    <div>
      <div>
        {/** {posts.posts.map((post) => ( */}
        {posts.posts.map(({ postID, caption, likes }) => (
          <article
            key={postID}
            style={{
              border: "2px solid black",
              padding: "15px",
              margin: "5px"
            }}
            className="post"
          >
            <div className="caption"> {caption} </div>
            <span className="likes" role="img">
              <button
                style={{
                  fontWeight: "bolder",
                  backgroundColor: "gray",
                  color: "white"
                }}
                onClick={() => dispatch(LikesClicked(postID))}
              >
                +
              </button>
              {likes}
              <button
                style={{
                  fontWeight: "bolder",
                  backgroundColor: "gray",
                  color: "white"
                }}
                onClick={() => dispatch(DisLikesClicked(postID))}
              >
                -
              </button>
              ❤️
            </span>
          </article>
        ))}
      </div>
    </div>
  );
}
