import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
import axios from "axios";

export const userDetails = createAsyncThunk("user/userDetails", 
  async (userId, { fulfillWithValue, rejectWithValue }) => {
    try {
      const { status, data } = await axios.post(
        `https://Social-Media.saurabhsharma11.repl.co/v1/userProfile/${userId}`
      );
      if (status === 201) {
        return fulfillWithValue(data);
      }
    } catch (error) {
      return rejectWithValue(error);
    }
  }
);

export const userSlice = createSlice({
    name: "users",
    initialState: {
        user: "",
        followers: [],
        following: [],
        status: "idle",
        error: null
    },
    reducers: { },
    extraReducers: {
      [userDetails.pending]: (state, action) => {
        state.status = "pending";
      },
      [userDetails.fulfilled]: (state, action) => {
        state.status = "success";
        state.user = action.payload;
      },
      [userDetails.rejected]: (state, action) => {
        state.status = "failed";
        state.error = action.error;
      },
    }
});

export const { } = userSlice.actions;

export default userSlice.reducer;
