import React,{useEffect,useState} from "react";
import { useSelector, useDispatch } from 'react-redux';
import { NavLink } from "react-router-dom";
import { unwrapResult } from "@reduxjs/toolkit";
import { viewPost } from "./postSlice";
import { LikeButton } from "./LikeButton";
import { Link, useParams, useLocation } from "react-router-dom";
import { postComment } from "./postSlice";

export function ViewPost() {
    const [pending, setPending] = useState("idle");
    const { postid } = useParams();
    const [showBox, setShowBox] = useState(false);
    const [comment, setComment] = useState("");
    const dispatch = useDispatch();
    const post = useSelector((state) => state.posts.singlePost);

    const handleOnclickHandler = (post) => {
        dispatch(postComment({ postid: post._id, comment }));
        setShowBox(false);
        setComment("");
    };

    useEffect(() => {
         (async () => {
            try {
                setPending("pending");
                const resultAction = await dispatch(viewPost(postid));
                unwrapResult(resultAction);
                setPending("success");
            } catch (error) {
                console.log(error);
            }
        })();
    }, [dispatch, postid] );

    return (
        <div>
            <NavLink className="NavLink" to="/">Feed</NavLink>
            <article
                key={post._id}
                style={{
                border: "2px solid black",
                padding: "15px",
                margin: "5px"
                }}
                className="post"
            >
                {post.image && <img src={post.image} alt=""/>}
                <LikeButton post={post} type="single" />
                {/** 
                    <LikeButton post={post}/>
                    <CommentOnPost post={post}/>
                */}
            </article>
        </div>
    )
}
