import React, { useState } from "react";

export function CommentOnPost(){
    const [ textBox, textBoxSetter ] = useState(true);
    
    function textBoxToggle(){
        console.log(textBox);
        if(textBox === true){
            textBoxSetter(false);
        } else {
            textBoxSetter(true);
        }
    }

    return (
        <div>
            <button onClick={() => textBoxToggle()}>Comment</button>
            <div>
                <input type="text" style={{ disabled:`${textBox}` }}/>
            </div>
        </div>
    )
}
