import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
import axios from "axios";

const fetchPosts = async () => {
  return axios.get("https://social-media-server.tanaypratap.repl.co/posts");
};

export const loadPosts = createAsyncThunk("posts/loadPosts", async () => {
  const response = await fetchPosts();
  return response.data;
});

export const postSlice = createSlice({
    name: "posts",
    initialState: {
        posts: [
            {
                postID: "p1201",
                caption: "learning redux",
                likes: 22,
                user: {
                    userID: "u1234",
                    name: "tanay"
                }
            },
            {
                postID: "p1202",
                caption: "it's frustrating to begin",
                likes: 24,
                user: {
                    userID: "u1234",
                    name: "tanay"
                }
            },
        ]
    },
    reducers: {
        postAdded(state, action) {
            state.push(action.payload)
        },
        LikesClicked: (state, action) => {
            const posts = state.posts.map((post) =>
                post.postID === action.payload
                ? { ...post, likes: post.likes + 1 }
                : post
            );
            return { ...state, posts };
        },
        DisLikesClicked: (state, action) => {
            const postIndex = state.posts.findIndex(
                (post) => post.postID === action.payload
            );
            state.posts[postIndex].likes -= 1;
        }
    },
    extraReducers: {
        [loadPosts.pending]: (state) => {
            state.status = "loading";
        },
        [loadPosts.fulfilled]: (state, { payload }) => {
            state.status = "fulfilled";
            state.posts = payload.posts;
        },
        [loadPosts.rejected]: (state, action) => {
            state.status = "error";
            state.error = action.error.message;
        }
    }
});

export const { LikesClicked, DisLikesClicked, postAdded } = postSlice.actions;
export default postSlice.reducer;
