import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { LikesClicked, DisLikesClicked, loadPosts } from "./postSlice";

export function AddPostInput() {
    const { status, posts } = useSelector((state) => state.posts);
    const dispatch = useDispatch();
    //const posts = useSelector((state) => { return state.posts});

    useEffect(() => {
        (async () => {
        if (status === "idle") {
            dispatch(loadPosts());
        }
        })();
    }, [status, dispatch]);

    return (
        <div>
            <div>
            {/** {posts.posts.map((post) => ( */}
            {posts.map(({ postID, caption, likes }) => (
                <article
                key={postID}
                style={{
                    border: "2px solid black",
                    padding: "15px",
                    margin: "5px"
                }}className="post">
                    <div className="caption">{caption}</div>
                    <span className="likes" role="img">
                        <button
                            style={{
                                width:"25px",
                                fontWeight: "bolder",
                                backgroundColor: "gray",
                                color: "white"
                            }}
                            onClick={() => dispatch(LikesClicked(postID))}
                        >+</button>
                        {likes}
                        <button
                            style={{
                                width:"25px",
                                fontWeight: "bolder",
                                backgroundColor: "gray",
                                color: "white"
                            }}
                            onClick={() => dispatch(DisLikesClicked(postID))}
                        >-</button>
                        ❤️
                    </span>
                </article>
            ))}
            </div>
        </div>
    );
}
