import React from "react";
import { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { follow, unFollow } from "./userSlice";

export const FollowToggle = ({ user }) => {
  const { currentUser } = useSelector((state) => state.auth);
  const dispatch = useDispatch();

  const userConnection = () => {
    if (user?.followers.includes(currentUser?.userid)) {
      dispatch(unFollow(user._id));
    } else {
      dispatch(follow(user._id));
    }
  };

  const buttonToggle = () => {
    if (user?.followers.includes(currentUser?.userid)) {
      return false;
    } else {
      return true;
    }
  };

  return (
    <button onClick={userConnection} className="follow-button rounded mb-4 focus:outline-none focus:ring">
      {buttonToggle() ? "Follow" : "Unfollow"}
    </button>
  );
};
