import React from "react";
import { useDispatch, useSelector } from "react-redux";

export const DeletePost = ({ user }) => {
    const { currentUser } = useSelector((state) => state.auth);
    const dispatch = useDispatch();
    console.log("delete",user,currentUser);
    const checkExistingUser = (currentUser, randomUser) => { return currentUser === randomUser };
  
    const renderDeletePost = checkExistingUser(currentUser.userid, user._id);

    const userConnection = () => {
        if ( user._id === currentUser?.userid ) {
            //dispatch(unFollow(user._id));
            console.log("user matched");
        } else {
            console.log("user unmatched");
        }
    };

    return (
        <>
            {renderDeletePost && 
                <button onClick={userConnection} className="follow-button flex items-center ring rounded mb-3 p-0.5">
                    <i class="material-icons">&#xe7fd;</i>
                </button>
            }
        </>
    );
};
