import React,{useEffect} from "react";
import { useDispatch, useSelector } from "react-redux";
import { loadPosts } from "./postSlice";



export const PostList = () => {
    const dispatch = useDispatch();
    const { status, posts, error } = useSelector((state) => state.posts);
    
    useEffect(() => {
        (async () => {
          if (status === "idle") {
            dispatch(loadPosts());
          }
        })();
      }, [status, dispatch]);

    const renderedPosts = posts.map((post) => (
      <article className="post-excerpt" key={post.id}>
        <p className="post-content">{post.content.substring(0, 100)}</p>
      </article>
    ))
  
    return (
      <section className="posts-list">
        <div>
            {/** {posts.posts.map((post) => ( */}
            {posts.map(({ postID, caption, likes }) => (
                <article
                    key={postID}
                    style={{
                        border: "2px solid black",
                        padding: "15px",
                        margin: "5px"
                    }} className="post"
                >

                <div className="caption"> {caption} </div>
                    <span className="likes" role="img">
                    <button
                        style={{
                            fontWeight: "bolder",
                            backgroundColor: "gray",
                            color: "white"
                        }}
                        onClick={() => dispatch(LikesClicked(postID))}
                    >+</button>
                        {likes}
                    <button
                        style={{
                            fontWeight: "bolder",
                            backgroundColor: "gray",
                            color: "white"
                        }}
                        onClick={() => dispatch(DisLikesClicked(postID))}>-
                    </button>
                        ❤️
                    </span>
                </article>
            ))}
        </div>
      </section>
    )
  }
