import React, { useEffect,useState } from "react";
import { Navigate, NavLink } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import { unwrapResult } from "@reduxjs/toolkit";
import { loadPosts } from "./postSlice";
import { NewPost } from "./NewPost";
import { PostBody } from "./PostBody";
import { LikeButton } from "./LikeButton";
import { CommentOnPost } from "./CommentButton";
import { ViewPost } from "./ViewPost";

export function PostList() {
    const [status, setStatus] = useState("idle");
    const { posts } = useSelector((state) => state.post);
    const dispatch = useDispatch();
    console.log(posts);

    useEffect(() => {
        (async () => {
            try {
                setStatus("pending");
                const result = await dispatch(loadPosts());
                unwrapResult(result);
                setStatus("success");
            } catch (error) {
                console.log(error);
                setStatus("idle");
            }
        })();
    }, [dispatch]);

    /* let renderContent =
        posts.length === 0 ? (
            <div className="text-center">
                <p className="p-2">No posts found</p>
            </div>
        ) : (
        React.Children.toArray(
            posts?.map((post) => <PostBody post={post} />)
        )
    ); */

    return (
        <div className="flex items-center flex-col mt-3 mb-5">
            <h1> PostList</h1>
            {/** <NewPost /> */}
            {/** {status === "pending" && "Loading..."} */}
            {/** {status === "success" && renderContent} */}
        </div>
    );
}
