import React,{ useState } from "react";

export function NewPost(){
    const [content, setContent] = useState('')
    const onContentChanged = e => setContent(e.target.value)
    return (
        <div>
            <h1>New Post</h1>
            <textarea
                id="postContent"
                name="postContent"
                value={content}
                onChange={onContentChanged}
            />
        </div>
    )
}
