import React from "react";
import { useSelector, useDispatch } from "react-redux";
import { likePost, unlikePost, loadPosts } from "./postSlice";

export function LikeButton({ post }){
    const dispatch = useDispatch();

    const likeButtonHandler = (post) => {
        dispatch(likePost(post._id));
    }

    return (
        <div>
            <div className="caption"> {post.desc} </div>
            <span className="likes" role="img">
                <button
                    style={{
                    fontWeight: "bolder",
                    backgroundColor: "gray",
                    color: "white",
                    padding:"5px"
                    }}
                    onClick={() => likeButtonHandler(post._id)}
                >
                    Like {post.likes.length}
                </button>
            </span>
        </div>
    )
}
