import React, { useState } from "react";
import { useDispatch } from 'react-redux';
import { nanoid } from '@reduxjs/toolkit'
import postAdded from "../posts/postSlice"

export function AddPostInput() {
    const [content, setContent] = useState('')

    const dispatch = useDispatch()

    const onContentChanged = (e) => setContent(e.target.value)

    const onSavePostClicked = () => {
        if (content) {
            dispatch(
                postAdded({
                    postID: nanoid(),
                    caption: content,
                    likes: 1,
                    user: {
                        userID: "u12346",
                        name: "Saurabh Sharma"
                    }
                })
            )
        }
    }

    return (
        <div>
            <div className="flex pl-2 py-3x">
                <textarea className="resize-auto rounded mx-3 my-1 p-1 w-full bg-gray-100" onChange={onContentChanged}></textarea>
            </div>
            <div className="flex justify-between mb-2 mt-2">
                <div className="flex items-center">
                    <label
                            htmlFor="file"
                            className="flex cursor-pointer items-center rounded p-2 ml-14 font-medium  hover:bg-gray-100">
                        <span className="text-gray-700">Add Photo</span>
                        <input
                            type="file"
                            name="file"
                            id="file"
                            accept="image/png, image/jpeg"
                            className="hidden"
                        />
                    </label>
                </div>
                <button onClick={onSavePostClicked}>Post</button>
            </div>
        </div>
    )
}
