import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
import axios from "axios";
import { toastSuccess } from "../Utils/ToastMessages";
import { setupAuthHeaderForServiceCalls } from "./serviceHandlers";
/*import { toastSuccess } from "../utils/toastMessage";
import { clearLocalStorage, setlocalStorage } from "./utils/localStorage"; */

export const userRegisteration = createAsyncThunk("auth/signup",async(values, { fulfillWithValue, rejectWithValue }) =>{
    try{
        const response = await axios.post("https://Social-Media.saurabhsharma11.repl.co/v1/userAuth/signup", values);
        if(response.status === 201){
            toastSuccess("User Registered Successfully");
        }
        return fulfillWithValue(response);
    }
    catch(error){
        return rejectWithValue(error.response);
    }
})

export const login = createAsyncThunk(
    "auth/login",
    async (values, { fulfillWithValue, rejectWithValue }) => {
      try {
        const response = await axios.post(
          "https://Social-Media.saurabhsharma11.repl.co/v1/userAuth/login",
          values
        );
  
        if (response.status === 200) {
          setupAuthHeaderForServiceCalls(response.data.token);
          setlocalStorage(response.data.userdata, response.data.token);
        }
        return fulfillWithValue(response.data);
      } catch (error) {
        return rejectWithValue(error.response);
      }
    }
  );
