import React from "react";
import { useSelector, useDispatch } from "react-redux";
import { likePost, DisLikeClicked, loadPosts } from "./postSlice";

export function PostLike({ post }){
    const { status, posts, error } = useSelector((state) => state.posts);
    const dispatch = useDispatch();

    return (
        <div>
            <div className="caption"> {post.desc} </div>
            <span className="likes" role="img">
                <button
                    style={{
                    fontWeight: "bolder",
                    backgroundColor: "gray",
                    color: "white",
                    padding:"5px"
                    }}
                    onClick={() => dispatch(LikeClicked(post._id))}
                >
                    Like {post.likes.length}
                </button>
            </span>
        </div>
    )
}
