import React from "react";
import { Link } from "react-router-dom";

export const UserData = ({ user }) => {
    return (
        <div className="ml-2">
            <div className="p-1">
                <p style={{textAlign:"left",fontStyle:"italic",fontWeight:"700",marginLeft:"3.5px"}}>Bio : {user.bio}</p>
            </div>
            <div className="flex text-gray-500 mt-1">
                {user.location && (
                    <div className="flex items-center">
                        <span className="pl-1">{user.location}</span>
                    </div>
                )}
                {user.url && (
                <div className="flex ml-2 items-center">
                    <a
                    href={`https://${user.url}`}
                    className="pl-1"
                    target="_blank"
                    rel="noreferrer">
                    {user.url}
                    </a>
                </div>
                )}
            </div>
            <div className="flex text-gray-600 mt-1 ml-1">
                <Link to="following">
                    {/** <span className="font-bold">{post.following.length}</span> */}
                    <span className="pl-1">post</span>
                </Link>
                <Link to="following">
                    <span className="font-bold">{user.following.length}</span>
                    <span className="pl-1">following</span>
                </Link>
                <Link to="followers" className="ml-2">
                    <span className="font-bold">{user.followers.length}</span>
                    <span className="pl-1">followers</span>
                </Link>
            </div>
        </div>
    );
};
