import React, { useState } from "react";

export function CommentOnPost(){
    const [ textbox, textBoxSetter ] = useState("hide");
    
    function textBoxToggle(){
        console.log("clicked",textbox);
        if(textbox === "hide"){
            textBoxSetter("show");
        } else {
            textBoxSetter("hide");
        }
    }

    return (
        <div>
            <button onClick={() => textBoxToggle()}>Comment</button>
            <input type="text" id="comment" style={{ display:`${textbox}`,backgroundColor:"gray" }}/>
        </div>
    )
}
