import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { LikesClicked, DisLikesClicked, loadPosts } from "./postSlice";
import { PostDislike } from "./postDisLike";

export function PostList() {
    const { status, posts, error } = useSelector((state) => state.posts);
    const dispatch = useDispatch();
    //const posts = useSelector((state) => { return state.posts});

    useEffect(() => {
        (async () => {
        if (status === "idle") {
            dispatch(loadPosts());
        }
        })();
    }, [status, dispatch]);
    

    return (
        <div>
            <div>
                {/** {posts.posts.map((post) => ( */}
                {posts.map((post) => (
                    <article
                        key={post._id}
                        style={{
                        border: "2px solid black",
                        padding: "15px",
                        margin: "5px"
                        }}
                        className="post"
                    >
                        {post.image && <img src={post.image} alt=""/>}
                        <div className="caption"> {post.desc} </div>
                            <span className="likes" role="img">
                            <button
                                style={{
                                fontWeight: "bolder",
                                backgroundColor: "gray",
                                color: "white",
                                padding:"5px"
                                }}
                                onClick={() => dispatch(LikesClicked(post._id))}
                            >
                                +
                            </button>
                            {post.likes}
                            <button
                                style={{
                                fontWeight: "bolder",
                                backgroundColor: "gray",
                                color: "white",
                                padding:"5px"
                                }}
                                onClick={() => dispatch(DisLikesClicked(post._id))}
                            >
                                -
                            </button>
                            ❤️
                            </span>
                    </article>
                ))}
            </div>
        </div>
    );
}
