import React, { useState } from "react";
import { useDispatch } from 'react-redux';
import postAdded from "../posts/postSlice"

export function AddPostInput() {
    const [content, setContent] = useState('')

    const dispatch = useDispatch()

    const onContentChanged = (e) => setContent(e.target.value)

    const onSavePostClicked = () => {
        if (content) {
            dispatch(
                postAdded({
                    id:'3',
                    title:"something new",
                    content,
                })
            )
        }
    }

    return (
        <section>
            <h1>Adding Post</h1>
            <form>
                <div>
                    <div className="flex pl-2 py-3x">
                        <textarea className="resize-auto rounded mx-3 my-1 p-1 w-full bg-gray-100" value={content}
                        onChange={onContentChanged}></textarea>
                    </div>
                    <div className="flex justify-between mb-2 mt-2">
                        <button type="button" onClick={onSavePostClicked}>Post</button>
                    </div>
                </div>
            </form>
        </section>
    )
}
