import React from "react";
import { useSelector, useDispatch } from "react-redux";
import { likePost, unlikePost } from "./postSlice";

export function LikeButton({ post, type }){
    const dispatch = useDispatch();

    const likeButtonHandler = (postid, postType) => {
        if(postType === "single"){
            dispatch(likePost(postid));
        }
        else {
            dispatch(unlikePost(postid));
        }
    }

    {/**
        const likeButtonHandler = (post) => {
    if (post?.likes.includes(currentUser.userid)) {
      if (type === "single") {
        dispatch(unLikeSinglePost(post._id));
      } else {
        dispatch(unlikePost(post._id));
      }
    } else {
      if (type === "single") {
        dispatch(likeSinglePost(post._id));
      } else {
        dispatch(likePost(post._id));
      }
    }
  };
    */}

    return (
        <div>
            <div className="caption"> {post.desc} </div>
            <span className="likes" role="img">
                <button
                    style={{
                    fontWeight: "bolder",
                    backgroundColor: "gray",
                    color: "white",
                    padding:"5px"
                    }}
                    onClick={() => likeButtonHandler(post._id,"single")}
                >
                    Like
                </button>
            </span>
        </div>
    )
}
