import React, { useState } from "react";

export function CommentOnPost(){
    const [ textBox, textBoxSetter ] = useState("hide");
    
    function textBoxToggle(){
        if(textBox === "hide"){
            textBoxSetter("show");
        } else {
            textBoxSetter("hide");
        }
    }

    return (
        <div>
            <button onClick={() => textBoxToggle()}>Comment</button>
            <div style={{display:`${textBox}`,backgroundColor:"gray"}}>
                <input type="text"/>
            </div>
        </div>
    )
}
