import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
import axios from "axios";

const fetchPosts = async () => {
    return axios.get("https://Social-Media.saurabhsharma11.repl.co/v1/posts");
};

export const loadPosts = createAsyncThunk("posts/loadPosts", async () => {
    const response = await fetchPosts();
    return response.data;
});

let current = new Date();
let cDate = current.getFullYear() + '-' + (current.getMonth() + 1) + '-' + current.getDate();
let cTime = current.getHours() + ":" + current.getMinutes() + ":" + current.getSeconds();
let dateTime = cDate + ',' + cTime;

export const createPost = createAsyncThunk("post/createPost",
    async ({ content,image }, { fulfillWithValue, rejectWithValue }) => {
        let postData = {
            userID: "123456",
            desc: content,
            status: "A",
            likes: 0,
            comments: [],
            createdDate: dateTime,
            modifiedData: dateTime
        };

        if (image) {
            const formData = new FormData();
            const fileName = Date().now + image.name;
            formData.append("image", image);
            formData.append("name", fileName);
            try {
                const { data, status } = await axios.post(
                    "https://social-media.saurabhsharma11.repl.co/v1/images/upload",
                    formData
                );
                if (status === 201) {
                    postData.image = data.url;
                }
            } catch (err) {
                console.log(err.message);
            }
        }

        try {
            const { data } = await axios.post(
            "https://social-media.saurabhsharma11.repl.co/v1/posts",
                postData
            );
            return fulfillWithValue(data.post);
        } catch (error) {
            return rejectWithValue(error.response);
        }
    }
);

export const postSlice = createSlice({
    name: "posts",
    initialState: {
        status: "idle",
        error: null,
        posts: []
    },
    reducers: {
        postAdded(state, action) {
            console.log(action.payload);
            console.log(state);
            state.posts.push(action.payload);
        },
        LikesClicked: (state, action) => {
            const posts = state.posts.map((post) =>
                post.postID === action.payload
                ? { ...post, likes: post.likes + 1 }
                : post
            );
            return { ...state, posts };
        },
        DisLikesClicked: (state, action) => {
            const postIndex = state.posts.findIndex(
                (post) => post.postID === action.payload
            );
            state.posts[postIndex].likes -= 1;
        }
    },
    extraReducers: {
        [createPost.fulfilled]: (state, action) => {
            state.posts.push(action.payload);
            console.log("reducer", action.payload.post);
        },
        [loadPosts.pending]: (state) => {
            state.status = "loading";
        },
        [loadPosts.fulfilled]: (state, { payload }) => {
            state.status = "fulfilled";
            state.posts = payload.posts;
        },
        [loadPosts.rejected]: (state, action) => {
            state.status = "error";
            state.error = action.error.message;
        }
    }
});

export const { LikesClicked, DisLikesClicked, postAdded } = postSlice.actions;
export default postSlice.reducer;
